solutionCode:
using System;

static int Fib(int n)
{
    if (n <= 1) return n;
    return Fib(n - 1) + Fib(n - 2);
}

Console.WriteLine(Fib(0)); // 0
Console.WriteLine(Fib(1)); // 1
Console.WriteLine(Fib(6)); // 8
