solutionCode:
const memo = {};

function fib(n) {
  if (n in memo) return memo[n];
  if (n <= 1) return n;
  memo[n] = fib(n - 1) + fib(n - 2);
  return memo[n];
}

console.log(fib(10)); // 55
console.log(fib(35)); // 9227465
